import React, {useContext} from 'react';
import { useQuery } from '@apollo/client';
import gql from 'graphql-tag';

function AccountPage(props) {
    var username = props.match.params.username
    console.log(useQuery(FETCH_USER_QUERY, {
        variables: {
            username: username
        }
    }));
    const user = useQuery(FETCH_USER_QUERY, {
        variables: {
            username: username
        }
    });
    let userText
    if (!user) {
        userText = <p>Loading user...</p>
    } else {
        const { username, name, email, createdPlatforms, playedPlatforms, bookmarkedPlatforms} = user;
    }
    return (
        <div>
            <h1>Account Page</h1>
            <p>Username: </p>
            <p>{user.username}</p>
        </div>
    );
}

export default AccountPage;

const FETCH_USER_QUERY = gql`
    query getUser(
        $username: String!
    ) {
        getUser(
            username: $username
        ) {
            username
            email
            name
            createdPlatforms
        }
    }
`
